-- Task
CREATE TABLE task (
  id BIGINT GENERATED BY DEFAULT AS IDENTITY (START WITH 1000) PRIMARY KEY,

  -- project_id BIGINT NOT NULL,

  -- Properties
  title VARCHAR(256) NOT NULL,
  done BOOL NOT NULL DEFAULT false
);

